{
  "name": "Focus - Strategic Vision Board",
  "description": "An Apple-inspired, AI-powered task management workspace acting as your Chief of Staff. It transforms vague ideas into structured, strategic plans using the Gemini API."
}

import React from 'react';
import ReactDOM from 'react-dom/client';
import App from './App';

const rootElement = document.getElementById('root');
if (!rootElement) {
  throw new Error("Could not find root element to mount to");
}

const root = ReactDOM.createRoot(rootElement);
root.render(
  <React.StrictMode>
    <App />
  </React.StrictMode>
);

export type Priority = 'P0' | 'P1' | 'P2' | 'P3';

export type Category = 'Work' | 'Life' | 'Creative';

export type Status = 'To Do' | 'In Progress' | 'Done';

export interface SubTask {
  id: string;
  title: string;
  completed: boolean;
}

export interface Task {
  id: string;
  title: string;
  description?: string;
  category: Category;
  priority: Priority;
  status: Status;
  duration?: string;
  subtasks: SubTask[];
  createdAt: number;
}

// AI Response Structure
export interface AIParsedTask {
  title: string;
  priority: Priority;
  duration: string;
  steps: string[];
}

export interface AIAnalysisResult {
  strategicAdvice: string;
  tasks: AIParsedTask[];
  coreGoal: string;
}

import { GoogleGenAI, Type } from "@google/genai";
import { AIAnalysisResult } from '../types';

const getAIClient = () => {
  const apiKey = process.env.API_KEY;
  if (!apiKey) {
    console.error("API Key is missing. Ensure process.env.API_KEY is set.");
    return null;
  }
  return new GoogleGenAI({ apiKey });
};

export const analyzeIdea = async (idea: string): Promise<AIAnalysisResult | null> => {
  const ai = getAIClient();
  if (!ai) return null;

  // Design Phase 1: Gemini acting as "Chief of Staff"
  const systemPrompt = `
    You are my Chief of Staff and Strategic Planner. 
    Your goal is not just to list to-dos, but to use the Eisenhower Matrix, GTD (Getting Things Done), and SMART principles to transform my vague input into a structured execution plan.

    My input might be messy. Identify the core contradiction or main goal ("Primary Conflict" or "Core Goal").
    Analyze the tasks based on Importance/Urgency.
    Break down large tasks into granular action items.
    Estimate time.
    Provide strategic advice on how to execute better or with less effort.

    Output MUST be a valid JSON object.
  `;

  try {
    const response = await ai.models.generateContent({
      model: "gemini-3-flash-preview",
      contents: `Input Idea: "${idea}"`,
      config: {
        systemInstruction: systemPrompt,
        responseMimeType: "application/json",
        responseSchema: {
          type: Type.OBJECT,
          properties: {
            coreGoal: { type: Type.STRING, description: "A one-sentence summary of the core objective." },
            strategicAdvice: { type: Type.STRING, description: "Strategic advice in Markdown format (bullet points allowed)." },
            tasks: {
              type: Type.ARRAY,
              items: {
                type: Type.OBJECT,
                properties: {
                  title: { type: Type.STRING },
                  priority: { type: Type.STRING, enum: ["P0", "P1", "P2", "P3"] },
                  duration: { type: Type.STRING, description: "Estimated time, e.g., '30m', '2h'" },
                  steps: { 
                    type: Type.ARRAY, 
                    items: { type: Type.STRING },
                    description: "3-5 granular sub-steps"
                  }
                },
                required: ["title", "priority", "duration", "steps"]
              }
            }
          },
          required: ["coreGoal", "strategicAdvice", "tasks"]
        }
      }
    });

    if (response.text) {
      return JSON.parse(response.text) as AIAnalysisResult;
    }
    return null;

  } catch (error) {
    console.error("Gemini Analysis Failed:", error);
    return null;
  }
};

import React from 'react';
import { Task, Priority } from '../types';
import { Clock, CheckCircle2, MoreHorizontal } from './Icons';

interface TaskCardProps {
  task: Task;
  onMove: (id: string, newStatus: Task['status']) => void;
  onDelete: (id: string) => void;
}

const PriorityBadge: React.FC<{ priority: Priority }> = ({ priority }) => {
  const colors = {
    'P0': 'bg-red-50 text-red-600 border-red-100',
    'P1': 'bg-orange-50 text-orange-600 border-orange-100',
    'P2': 'bg-blue-50 text-blue-600 border-blue-100',
    'P3': 'bg-gray-50 text-gray-500 border-gray-100',
  };

  return (
    <span className={`text-[10px] font-semibold px-2 py-0.5 rounded-full border ${colors[priority]}`}>
      {priority}
    </span>
  );
};

export const TaskCard: React.FC<TaskCardProps> = ({ task, onMove, onDelete }) => {
  return (
    <div className="group bg-white p-4 rounded-2xl shadow-[0_2px_8px_rgba(0,0,0,0.04)] hover:shadow-[0_4px_12px_rgba(0,0,0,0.08)] transition-all duration-300 border border-gray-100/50 flex flex-col gap-3 relative animate-in fade-in slide-in-from-bottom-2">

      {/* Header */}
      <div className="flex justify-between items-start">
        <div className="flex gap-2 items-center">
          <PriorityBadge priority={task.priority} />
          {task.duration && (
            <span className="flex items-center gap-1 text-[10px] text-gray-400 font-medium">
              <Clock size={10} />
              {task.duration}
            </span>
          )}
        </div>
        <button 
          onClick={() => onDelete(task.id)}
          className="text-gray-300 hover:text-red-500 transition-colors opacity-0 group-hover:opacity-100"
        >
          <MoreHorizontal size={16} />
        </button>
      </div>

      {/* Content */}
      <div>
        <h3 className="text-gray-900 font-semibold text-sm leading-snug mb-1">{task.title}</h3>
        {task.description && (
          <p className="text-gray-500 text-xs line-clamp-2">{task.description}</p>
        )}
      </div>

      {/* Subtasks Preview (if any) */}
      {task.subtasks.length > 0 && (
        <div className="bg-gray-50/50 rounded-lg p-2 space-y-1">
          {task.subtasks.slice(0, 2).map(st => (
            <div key={st.id} className="flex items-center gap-2 text-xs text-gray-500">
               <div className={`w-1.5 h-1.5 rounded-full ${st.completed ? 'bg-green-400' : 'bg-gray-300'}`} />
               <span className={st.completed ? 'line-through opacity-60' : ''}>{st.title}</span>
            </div>
          ))}
          {task.subtasks.length > 2 && (
            <div className="text-[10px] text-gray-400 pl-3.5">+{task.subtasks.length - 2} more steps</div>
          )}
        </div>
      )}

      {/* Footer Actions */}
      <div className="flex items-center justify-between pt-1 border-t border-gray-50 mt-1">
        <span className="text-[10px] font-semibold text-gray-400 uppercase tracking-wider">{task.category}</span>

        {/* Simple State Mover for MVP */}
        <div className="flex gap-1">
           {task.status !== 'To Do' && (
             <button onClick={() => onMove(task.id, 'To Do')} className="w-6 h-6 rounded-full hover:bg-gray-100 flex items-center justify-center text-gray-400" title="Move to Todo">
               <div className="w-2 h-2 rounded-full border border-gray-400" />
             </button>
           )}
           {task.status !== 'In Progress' && (
             <button onClick={() => onMove(task.id, 'In Progress')} className="w-6 h-6 rounded-full hover:bg-blue-50 flex items-center justify-center text-blue-500" title="Move to In Progress">
               <div className="w-2 h-2 rounded-full bg-blue-400" />
             </button>
           )}
           {task.status !== 'Done' && (
             <button onClick={() => onMove(task.id, 'Done')} className="w-6 h-6 rounded-full hover:bg-green-50 flex items-center justify-center text-green-500" title="Mark Done">
               <CheckCircle2 size={14} />
             </button>
           )}
        </div>
      </div>
    </div>
  );
};

import React, { useState, useEffect } from 'react';
import { Sparkles, BrainCircuit, X, ChevronRight, CheckCircle2 } from './Icons';
import { analyzeIdea } from '../services/geminiService';
import { AIAnalysisResult } from '../types';

interface ChiefOfStaffModalProps {
  isOpen: boolean;
  onClose: () => void;
  onPlanCreated: (result: AIAnalysisResult) => void;
}

export const ChiefOfStaffModal: React.FC<ChiefOfStaffModalProps> = ({ isOpen, onClose, onPlanCreated }) => {
  const [input, setInput] = useState('');
  const [isThinking, setIsThinking] = useState(false);
  const [result, setResult] = useState<AIAnalysisResult | null>(null);

  // Reset state when opening
  useEffect(() => {
    if (isOpen && !result) {
      setInput('');
    }
  }, [isOpen, result]);

  const handleSubmit = async () => {
    if (!input.trim()) return;
    setIsThinking(true);
    const data = await analyzeIdea(input);
    setResult(data);
    setIsThinking(false);
  };

  const handleApply = () => {
    if (result) {
      onPlanCreated(result);
      onClose();
      // Small delay to clear state for next time
      setTimeout(() => setResult(null), 500);
    }
  };

  if (!isOpen) return null;

  return (
    <div className="fixed inset-0 z-50 flex items-center justify-center p-4">
      {/* Backdrop */}
      <div className="absolute inset-0 bg-gray-900/20 backdrop-blur-sm transition-opacity" onClick={onClose} />

      {/* Modal Content */}
      <div className="relative w-full max-w-2xl bg-white/95 backdrop-blur-xl rounded-[32px] shadow-2xl border border-white/50 overflow-hidden flex flex-col max-h-[85vh]">

        {/* Header */}
        <div className="p-8 pb-4 flex justify-between items-center border-b border-gray-100/50">
          <div className="flex items-center gap-3">
             <div className="w-10 h-10 rounded-full bg-gradient-to-tr from-indigo-500 to-purple-500 flex items-center justify-center text-white shadow-lg shadow-indigo-200">
               <BrainCircuit size={20} />
             </div>
             <div>
               <h2 className="text-xl font-bold text-gray-900 tracking-tight">Chief of Staff</h2>
               <p className="text-sm text-gray-500 font-medium">Strategic Planning & Clarification</p>
             </div>
          </div>
          <button onClick={onClose} className="p-2 rounded-full hover:bg-gray-100 text-gray-400 hover:text-gray-600 transition-colors">
            <X size={20} />
          </button>
        </div>

        {/* Body */}
        <div className="p-8 pt-6 overflow-y-auto flex-1">
          {!result ? (
            <div className="flex flex-col gap-6 h-full justify-center">
              <div className="space-y-2 text-center">
                <h3 className="text-2xl font-semibold text-gray-800">What's on your mind?</h3>
                <p className="text-gray-500">Dump your messy thoughts, vague goals, or anxieties here. I'll structure them using Eisenhower & GTD principles.</p>
              </div>

              <div className="relative">
                <textarea
                  className="w-full h-40 bg-gray-50 rounded-2xl p-6 text-lg text-gray-800 placeholder-gray-300 resize-none focus:outline-none focus:ring-2 focus:ring-indigo-500/20 transition-all border border-gray-100"
                  placeholder="e.g., 'I really need to fix my sleep schedule and also launch that creative project but I'm overwhelmed...'"
                  value={input}
                  onChange={(e) => setInput(e.target.value)}
                  disabled={isThinking}
                />
                {isThinking && (
                  <div className="absolute inset-0 bg-white/50 backdrop-blur-[2px] rounded-2xl flex items-center justify-center">
                    <div className="flex flex-col items-center gap-3">
                      <div className="w-6 h-6 border-2 border-indigo-500 border-t-transparent rounded-full animate-spin" />
                      <span className="text-sm font-medium text-indigo-600 animate-pulse">Analyzing Priorities...</span>
                    </div>
                  </div>
                )}
              </div>

              <div className="flex justify-end">
                <button 
                  onClick={handleSubmit}
                  disabled={!input.trim() || isThinking}
                  className="bg-black text-white px-8 py-3 rounded-full font-semibold hover:scale-[1.02] active:scale-[0.98] transition-all disabled:opacity-50 disabled:scale-100 shadow-xl flex items-center gap-2"
                >
                  <Sparkles size={18} />
                  Analyze & Plan
                </button>
              </div>
            </div>
          ) : (
            <div className="space-y-6 animate-in fade-in slide-in-from-bottom-4">
              {/* Core Goal Section */}
              <div className="bg-indigo-50/50 p-6 rounded-2xl border border-indigo-100">
                <h4 className="text-xs font-bold text-indigo-500 uppercase tracking-widest mb-2">Core Objective</h4>
                <p className="text-xl font-medium text-indigo-900 leading-relaxed">"{result.coreGoal}"</p>
              </div>

              {/* Strategy Section */}
              <div className="space-y-3">
                <h4 className="text-sm font-bold text-gray-900 flex items-center gap-2">
                  <span className="w-1.5 h-1.5 rounded-full bg-gray-900" />
                  Strategic Advice
                </h4>
                <div className="text-gray-600 text-sm leading-relaxed whitespace-pre-line bg-gray-50 p-5 rounded-xl">
                  {result.strategicAdvice}
                </div>
              </div>

              {/* Proposed Tasks Preview */}
              <div className="space-y-3">
                <h4 className="text-sm font-bold text-gray-900 flex items-center gap-2">
                   <span className="w-1.5 h-1.5 rounded-full bg-gray-900" />
                   Action Plan ({result.tasks.length} items)
                </h4>
                <div className="grid grid-cols-1 sm:grid-cols-2 gap-3">
                  {result.tasks.map((task, idx) => (
                    <div key={idx} className="bg-white border border-gray-100 p-3 rounded-xl shadow-sm flex flex-col gap-1">
                      <div className="flex justify-between items-start">
                         <span className="font-medium text-gray-800 text-sm">{task.title}</span>
                         <span className="text-[10px] bg-gray-100 px-1.5 py-0.5 rounded text-gray-600">{task.priority}</span>
                      </div>
                      <span className="text-[11px] text-gray-400">{task.duration} • {task.steps.length} steps</span>
                    </div>
                  ))}
                </div>
              </div>
            </div>
          )}
        </div>

        {/* Footer (Only valid when result exists) */}
        {result && (
          <div className="p-6 border-t border-gray-100 bg-gray-50/50 flex justify-between items-center">
             <button onClick={() => setResult(null)} className="text-gray-500 hover:text-gray-800 text-sm font-medium px-4">
               Back to Input
             </button>
             <button 
               onClick={handleApply}
               className="bg-indigo-600 text-white px-8 py-3 rounded-full font-semibold hover:bg-indigo-700 transition-colors shadow-lg shadow-indigo-200 flex items-center gap-2"
             >
               <CheckCircle2 size={18} />
               Accept Plan
             </button>
          </div>
        )}
      </div>
    </div>
  );
};

import React, { useState, useMemo } from 'react';
import { 
  LayoutGrid, 
  Briefcase, 
  Coffee, 
  Palette, 
  Plus, 
  Sparkles 
} from './components/Icons';
import { Task, Category, Status, AIAnalysisResult } from './types';
import { TaskCard } from './components/TaskCard';
import { ChiefOfStaffModal } from './components/ChiefOfStaffModal';

const App: React.FC = () => {
  // --- State ---
  const [tasks, setTasks] = useState<Task[]>([
    {
      id: '1',
      title: 'Review Quarterly Goals',
      category: 'Work',
      priority: 'P1',
      status: 'To Do',
      duration: '45m',
      subtasks: [],
      createdAt: Date.now()
    },
    {
      id: '2',
      title: 'Sketch new logo concepts',
      category: 'Creative',
      priority: 'P2',
      status: 'In Progress',
      duration: '2h',
      subtasks: [{ id: 's1', title: 'Moodboard', completed: true }, { id: 's2', title: 'Drafts', completed: false }],
      createdAt: Date.now()
    }
  ]);

  const [activeCategory, setActiveCategory] = useState<Category | 'All'>('All');
  const [isAIModalOpen, setAIModalOpen] = useState(false);

  // --- Computed ---
  const filteredTasks = useMemo(() => {
    return activeCategory === 'All' 
      ? tasks 
      : tasks.filter(t => t.category === activeCategory);
  }, [tasks, activeCategory]);

  const columns: Status[] = ['To Do', 'In Progress', 'Done'];

  // --- Handlers ---
  const handleMoveTask = (id: string, newStatus: Status) => {
    setTasks(prev => prev.map(t => t.id === id ? { ...t, status: newStatus } : t));
  };

  const handleDeleteTask = (id: string) => {
     setTasks(prev => prev.filter(t => t.id !== id));
  };

  const handleAIPlanCreated = (result: AIAnalysisResult) => {
    // Convert AI tasks to App tasks
    const newTasks: Task[] = result.tasks.map((aiTask, index) => ({
      id: `ai-${Date.now()}-${index}`,
      title: aiTask.title,
      description: `Strategic focus derived from: "${result.coreGoal}"`,
      category: activeCategory === 'All' ? 'Work' : activeCategory, // Default to current view or Work
      priority: aiTask.priority,
      status: 'To Do',
      duration: aiTask.duration,
      subtasks: aiTask.steps.map((step, i) => ({ id: `st-${i}`, title: step, completed: false })),
      createdAt: Date.now()
    }));

    setTasks(prev => [...newTasks, ...prev]);
  };

  // --- Render Helpers ---
  const renderNavButton = (cat: Category | 'All', icon: React.ReactNode, label: string) => {
    const isActive = activeCategory === cat;
    return (
      <button 
        onClick={() => setActiveCategory(cat)}
        className={`
          flex items-center gap-3 w-full px-4 py-3 rounded-xl transition-all duration-200 group
          ${isActive 
            ? 'bg-white shadow-sm text-gray-900 font-semibold' 
            : 'text-gray-500 hover:bg-white/50 hover:text-gray-700'
          }
        `}
      >
        <span className={`${isActive ? 'text-indigo-600' : 'text-gray-400 group-hover:text-gray-500'}`}>
          {icon}
        </span>
        <span className="text-sm">{label}</span>
        {isActive && <div className="ml-auto w-1.5 h-1.5 rounded-full bg-indigo-600" />}
      </button>
    );
  };

  return (
    <div className="flex h-screen w-full bg-[#F5F5F7] text-gray-900 overflow-hidden font-sans">

      {/* Sidebar - Glassmorphism */}
      <aside className="w-64 h-full hidden md:flex flex-col p-6 border-r border-gray-200/60 bg-[#F5F5F7]/50 backdrop-blur-md">
        <div className="mb-10 pl-2">
          <h1 className="text-2xl font-bold tracking-tight text-gray-900 flex items-center gap-2">
             <div className="w-3 h-3 bg-black rounded-full" />
             Focus.
          </h1>
          <p className="text-xs text-gray-400 font-medium mt-1 ml-5 tracking-wide">PHASE 1: CLARITY</p>
        </div>

        <nav className="space-y-2 flex-1">
          {renderNavButton('All', <LayoutGrid size={18} />, 'Overview')}
          <div className="pt-4 pb-2 pl-4 text-[10px] font-bold text-gray-400 uppercase tracking-widest">Domains</div>
          {renderNavButton('Work', <Briefcase size={18} />, 'Work')}
          {renderNavButton('Life', <Coffee size={18} />, 'Life')}
          {renderNavButton('Creative', <Palette size={18} />, 'Creative')}
        </nav>

        <div className="mt-auto">
          <button 
            onClick={() => setAIModalOpen(true)}
            className="w-full bg-gradient-to-r from-gray-900 to-gray-800 text-white rounded-2xl p-4 shadow-lg shadow-gray-200 hover:shadow-xl hover:scale-[1.02] transition-all duration-300 group relative overflow-hidden"
          >
            <div className="absolute inset-0 bg-white/10 opacity-0 group-hover:opacity-20 transition-opacity" />
            <div className="flex items-center justify-between relative z-10">
              <div className="flex flex-col items-start">
                <span className="font-bold text-sm">Chief of Staff</span>
                <span className="text-[10px] opacity-70">Clarify your mind</span>
              </div>
              <Sparkles size={18} className="text-yellow-200" />
            </div>
          </button>
        </div>
      </aside>

      {/* Main Content */}
      <main className="flex-1 h-full overflow-hidden flex flex-col relative">
        {/* Mobile Header */}
        <div className="md:hidden flex items-center justify-between p-6 pb-2">
          <h1 className="text-2xl font-bold">Focus.</h1>
          <button onClick={() => setAIModalOpen(true)} className="p-2 bg-black text-white rounded-full">
            <Sparkles size={18} />
          </button>
        </div>

        {/* Board Header */}
        <header className="px-8 py-6 flex items-end justify-between shrink-0">
          <div>
            <h2 className="text-3xl font-bold text-gray-900 tracking-tight mb-1">
              {activeCategory === 'All' ? 'Overview' : activeCategory}
            </h2>
            <p className="text-gray-500 font-medium text-sm">
              {filteredTasks.length} tasks pending • Keep it simple.
            </p>
          </div>

          <button 
             className="hidden md:flex items-center gap-2 bg-white px-5 py-2.5 rounded-full shadow-sm hover:shadow-md border border-gray-100 transition-all text-sm font-semibold text-gray-700"
             onClick={() => {/* Standard add task - can be implemented later */}}
          >
            <Plus size={16} />
            New Task
          </button>
        </header>

        {/* Kanban Board */}
        <div className="flex-1 overflow-x-auto overflow-y-hidden px-8 pb-8">
          <div className="flex h-full gap-8 min-w-[800px]">
            {columns.map(status => (
              <div key={status} className="flex-1 flex flex-col min-w-[300px] h-full">
                {/* Column Header */}
                <div className="flex items-center justify-between mb-4 px-1">
                  <h3 className="text-sm font-bold text-gray-500 uppercase tracking-wider">{status}</h3>
                  <span className="text-xs font-semibold bg-gray-200/50 text-gray-500 px-2 py-0.5 rounded-md">
                    {filteredTasks.filter(t => t.status === status).length}
                  </span>
                </div>

                {/* Column Content */}
                <div className="flex-1 overflow-y-auto pr-2 pb-4 space-y-3 scrollbar-hide">
                  {filteredTasks
                    .filter(t => t.status === status)
                    .map(task => (
                      <TaskCard 
                        key={task.id} 
                        task={task} 
                        onMove={handleMoveTask}
                        onDelete={handleDeleteTask}
                      />
                    ))
                  }

                  {/* Empty State for Column */}
                  {filteredTasks.filter(t => t.status === status).length === 0 && (
                    <div className="h-32 border-2 border-dashed border-gray-200 rounded-2xl flex items-center justify-center text-gray-300 text-sm font-medium">
                      Empty
                    </div>
                  )}
                </div>
              </div>
            ))}
          </div>
        </div>
      </main>

      {/* AI Modal */}
      <ChiefOfStaffModal 
        isOpen={isAIModalOpen} 
        onClose={() => setAIModalOpen(false)}
        onPlanCreated={handleAIPlanCreated}
      />
    </div>
  );
};

export default App;

{
  "name": "Focus - 战略视野看板",
  "description": "受苹果设计启发的 AI 驱动任务管理工作区，作为您的幕僚长，利用 Gemini API 将模糊的想法转化为结构化的战略计划。",
  "requestFramePermissions": []
}

export type Priority = 'P0' | 'P1' | 'P2' | 'P3';

export type Category = '工作' | '生活' | '创意';

export type Status = '待办' | '进行中' | '已完成';

export interface SubTask {
  id: string;
  title: string;
  completed: boolean;
}

export interface Task {
  id: string;
  title: string;
  description?: string;
  category: Category;
  priority: Priority;
  status: Status;
  duration?: string;
  subtasks: SubTask[];
  createdAt: number;
}

// AI Response Structure
export interface AIParsedTask {
  title: string;
  priority: Priority;
  duration: string;
  steps: string[];
}

export interface AIAnalysisResult {
  strategicAdvice: string;
  tasks: AIParsedTask[];
  coreGoal: string;
}

import { GoogleGenAI, Type } from "@google/genai";
import { AIAnalysisResult } from '../types';

const getAIClient = () => {
  const apiKey = process.env.API_KEY;
  if (!apiKey) {
    console.error("API Key 缺失。请确保 process.env.API_KEY 已设置。");
    return null;
  }
  return new GoogleGenAI({ apiKey });
};

export const analyzeIdea = async (idea: string): Promise<AIAnalysisResult | null> => {
  const ai = getAIClient();
  if (!ai) return null;

  // Design Phase 2: Localized Chinese "Chief of Staff"
  const systemPrompt = `
    你是我的“幕僚长”和战略规划师。
    你的目标不仅是列出待办事项，而是利用“艾森豪威尔矩阵”、“GTD（Getting Things Done）”和“SMART原则”将我输入的模糊想法转化为结构化的中文执行方案。

    我的输入可能很乱。请识别核心矛盾或主要目标（“核心目标”）。
    根据重要性/紧急性分析任务。
    将大任务拆解为颗粒度细致的行动项。
    预估时间。
    提供关于如何执行得更好或更省力的战略建议。

    输出必须是有效的 JSON 对象，并且所有文本内容（除了 JSON 键名和 Priority 枚举值）必须完全使用中文。
  `;

  try {
    const response = await ai.models.generateContent({
      model: "gemini-3-flash-preview",
      contents: `我的想法: "${idea}"`,
      config: {
        systemInstruction: systemPrompt,
        responseMimeType: "application/json",
        responseSchema: {
          type: Type.OBJECT,
          properties: {
            coreGoal: { type: Type.STRING, description: "一句话总结核心目标（中文）" },
            strategicAdvice: { type: Type.STRING, description: "战略建议，使用 Markdown 格式（中文）" },
            tasks: {
              type: Type.ARRAY,
              items: {
                type: Type.OBJECT,
                properties: {
                  title: { type: Type.STRING, description: "任务标题（中文）" },
                  priority: { type: Type.STRING, enum: ["P0", "P1", "P2", "P3"] },
                  duration: { type: Type.STRING, description: "预估时间，如 '30分钟', '2小时'" },
                  steps: { 
                    type: Type.ARRAY, 
                    items: { type: Type.STRING },
                    description: "3-5 个具体的子步骤（中文）"
                  }
                },
                required: ["title", "priority", "duration", "steps"]
              }
            }
          },
          required: ["coreGoal", "strategicAdvice", "tasks"]
        }
      }
    });

    if (response.text) {
      return JSON.parse(response.text) as AIAnalysisResult;
    }
    return null;

  } catch (error) {
    console.error("Gemini 分析失败:", error);
    return null;
  }
};

