const App: React.FC = () => {
  // --- State ---
  const [tasks, setTasks] = useState<Task[]>([
    {
      id: '1',
      title: '回顾季度目标',
      category: '工作',
      priority: 'P1',
      status: '待办',
      duration: '45分钟',
      subtasks: [],
      createdAt: Date.now()
    },
    {
      id: '2',
      title: '绘制新 Logo 概念草图',
      category: '创意',
      priority: 'P2',
      status: '进行中',
      duration: '2小时',
      subtasks: [{ id: 's1', title: '情绪板收集', completed: true }, { id: 's2', title: '草图绘制', completed: false }],
      createdAt: Date.now()
    }
  ]);

  const [activeCategory, setActiveCategory] = useState<Category | '全部'>('全部');
  const [isAIModalOpen, setAIModalOpen] = useState(false);

  // --- Computed ---
  const filteredTasks = useMemo(() => {
    return activeCategory === '全部' 
      ? tasks 
      : tasks.filter(t => t.category === activeCategory);
  }, [tasks, activeCategory]);

  const columns: Status[] = ['待办', '进行中', '已完成'];

  // --- Handlers ---
  const handleMoveTask = (id: string, newStatus: Status) => {
    setTasks(prev => prev.map(t => t.id === id ? { ...t, status: newStatus } : t));
  };

  const handleDeleteTask = (id: string) => {
     setTasks(prev => prev.filter(t => t.id !== id));
  };

  const handleAIPlanCreated = (result: AIAnalysisResult) => {
    // Convert AI tasks to App tasks
    const newTasks: Task[] = result.tasks.map((aiTask, index) => ({
      id: `ai-${Date.now()}-${index}`,
      title: aiTask.title,
      description: `来源于战略目标: "${result.coreGoal}"`,
      category: activeCategory === '全部' ? '工作' : activeCategory, // Default to current view or Work
      priority: aiTask.priority,
      status: '待办',
      duration: aiTask.duration,
      subtasks: aiTask.steps.map((step, i) => ({ id: `st-${i}`, title: step, completed: false })),
      createdAt: Date.now()
    }));

    setTasks(prev => [...newTasks, ...prev]);
  };

