        <nav className="space-y-2 flex-1">
          {renderNavButton('全部', <LayoutGrid size={18} />, '总览')}
          <div className="pt-4 pb-2 pl-4 text-[10px] font-bold text-gray-400 uppercase tracking-widest">领域</div>
          {renderNavButton('工作', <Briefcase size={18} />, '工作')}
          {renderNavButton('生活', <Coffee size={18} />, '生活')}
          {renderNavButton('创意', <Palette size={18} />, '创意')}
        </nav>

        <div className="mt-auto">
          <button 
            onClick={() => setAIModalOpen(true)}
            className="w-full bg-gradient-to-r from-gray-900 to-gray-800 text-white rounded-2xl p-4 shadow-lg shadow-gray-200 hover:shadow-xl hover:scale-[1.02] transition-all duration-300 group relative overflow-hidden"
          >
            <div className="absolute inset-0 bg-white/10 opacity-0 group-hover:opacity-20 transition-opacity" />
            <div className="flex items-center justify-between relative z-10">
              <div className="flex flex-col items-start">
                <span className="font-bold text-sm">幕僚长</span>
                <span className="text-[10px] opacity-70">梳理您的思绪</span>
              </div>
              <Sparkles size={18} className="text-yellow-200" />
            </div>
          </button>
        </div>
      </aside>

      {/* Main Content */}
      <main className="flex-1 h-full overflow-hidden flex flex-col relative">
        {/* Mobile Header */}
        <div className="md:hidden flex items-center justify-between p-6 pb-2">
          <h1 className="text-2xl font-bold">Focus.</h1>
          <button onClick={() => setAIModalOpen(true)} className="p-2 bg-black text-white rounded-full">
            <Sparkles size={18} />
          </button>
        </div>

        {/* Board Header */}
        <header className="px-8 py-6 flex items-end justify-between shrink-0">
          <div>
            <h2 className="text-3xl font-bold text-gray-900 tracking-tight mb-1">
              {activeCategory === '全部' ? '总览' : activeCategory}
            </h2>
            <p className="text-gray-500 font-medium text-sm">
              {filteredTasks.length} 个待办任务 • 保持专注 (Stay Focused)
            </p>
          </div>

          <button 
             className="hidden md:flex items-center gap-2 bg-white px-5 py-2.5 rounded-full shadow-sm hover:shadow-md border border-gray-100 transition-all text-sm font-semibold text-gray-700"
             onClick={() => {/* Standard add task - can be implemented later */}}
          >
            <Plus size={16} />
            新建任务
          </button>
        </header>

        {/* Kanban Board */}
        <div className="flex-1 overflow-x-auto overflow-y-hidden px-8 pb-8">
          <div className="flex h-full gap-8 min-w-[800px]">
            {columns.map(status => (
              <div key={status} className="flex-1 flex flex-col min-w-[300px] h-full">
