import React, { useState, useMemo } from 'react';
import { 
  LayoutGrid, 
  Briefcase, 
  Coffee, 
  Palette, 
  Plus
} from './components/Icons';
import { Task, Category, Status } from './types';
import { TaskCard } from './components/TaskCard';
import { NewTaskModal } from './components/NewTaskModal';

const App: React.FC = () => {
  // --- State ---
  const [tasks, setTasks] = useState<Task[]>([
    {
      id: '1',
      title: '回顾季度目标',
      category: '工作',
      priority: 'P1',
      status: '待办',
      duration: '45分钟',
      subtasks: [],
      createdAt: Date.now()
    },
    {
      id: '2',
      title: '绘制新 Logo 概念草图',
      category: '创意',
      priority: 'P2',
      status: '进行中',
      duration: '2小时',
      subtasks: [{ id: 's1', title: '情绪板收集', completed: true }, { id: 's2', title: '草图绘制', completed: false }],
      createdAt: Date.now()
    }
  ]);

  const [activeCategory, setActiveCategory] = useState<Category | '全部'>('全部');
  const [isNewTaskModalOpen, setNewTaskModalOpen] = useState(false);

  // --- Computed ---
  const filteredTasks = useMemo(() => {
    return activeCategory === '全部' 
      ? tasks 
      : tasks.filter(t => t.category === activeCategory);
  }, [tasks, activeCategory]);

  const columns: Status[] = ['待办', '进行中', '已完成'];

  // --- Handlers ---
  const handleMoveTask = (id: string, newStatus: Status) => {
    setTasks(prev => prev.map(t => t.id === id ? { ...t, status: newStatus } : t));
  };

  const handleDeleteTask = (id: string) => {
     setTasks(prev => prev.filter(t => t.id !== id));
  };

  const handleCreateTask = (taskData: Omit<Task, 'id' | 'createdAt' | 'status' | 'subtasks'>) => {
    const newTask: Task = {
      id: `task-${Date.now()}`,
      createdAt: Date.now(),
      status: '待办',
      subtasks: [], // Default empty subtasks for manual creation
      ...taskData
    };
    setTasks(prev => [newTask, ...prev]);
  };

  // --- Render Helpers ---
  const renderNavButton = (cat: Category | '全部', icon: React.ReactNode, label: string) => {
    const isActive = activeCategory === cat;
    return (
      <button 
        onClick={() => setActiveCategory(cat)}
        className={`
          flex items-center gap-3 w-full px-4 py-3 rounded-xl transition-all duration-200 group
          ${isActive 
            ? 'bg-white shadow-sm text-gray-900 font-semibold' 
            : 'text-gray-500 hover:bg-white/50 hover:text-gray-700'
          }
        `}
      >
        <span className={`${isActive ? 'text-indigo-600' : 'text-gray-400 group-hover:text-gray-500'}`}>
          {icon}
        </span>
        <span className="text-sm">{label}</span>
        {isActive && <div className="ml-auto w-1.5 h-1.5 rounded-full bg-indigo-600" />}
      </button>
    );
  };

  return (
    <div className="flex h-screen w-full bg-[#F5F5F7] text-gray-900 overflow-hidden font-sans">

      {/* Sidebar - Glassmorphism */}
      <aside className="w-64 h-full hidden md:flex flex-col p-6 border-r border-gray-200/60 bg-[#F5F5F7]/50 backdrop-blur-md">
        <div className="mb-10 pl-2">
          <h1 className="text-2xl font-bold tracking-tight text-gray-900 flex items-center gap-2">
             <div className="w-3 h-3 bg-black rounded-full" />
             Focus.
          </h1>
          <p className="text-xs text-gray-400 font-medium mt-1 ml-5 tracking-wide">二阶段：本地化</p>
        </div>

        <nav className="space-y-2 flex-1">
          {renderNavButton('全部', <LayoutGrid size={18} />, '总览')}
          <div className="pt-4 pb-2 pl-4 text-[10px] font-bold text-gray-400 uppercase tracking-widest">领域</div>
          {renderNavButton('工作', <Briefcase size={18} />, '工作')}
          {renderNavButton('生活', <Coffee size={18} />, '生活')}
          {renderNavButton('创意', <Palette size={18} />, '创意')}
        </nav>

        <div className="mt-auto">
          {/* Sidebar bottom area can be used for settings later */}
        </div>
      </aside>

      {/* Main Content */}
      <main className="flex-1 h-full overflow-hidden flex flex-col relative">
        {/* Mobile Header */}
        <div className="md:hidden flex items-center justify-between p-6 pb-2">
          <h1 className="text-2xl font-bold">Focus.</h1>
          <button onClick={() => setNewTaskModalOpen(true)} className="p-2 bg-black text-white rounded-full shadow-lg">
            <Plus size={20} />
          </button>
        </div>

        {/* Board Header */}
        <header className="px-8 py-6 flex items-end justify-between shrink-0">
          <div>
            <h2 className="text-3xl font-bold text-gray-900 tracking-tight mb-1">
              {activeCategory === '全部' ? '总览' : activeCategory}
            </h2>
            <p className="text-gray-500 font-medium text-sm">
              {filteredTasks.length} 个待办任务 • 保持专注 (Stay Focused)
            </p>
          </div>

          <button 
             className="hidden md:flex items-center gap-2 bg-black text-white px-5 py-2.5 rounded-full shadow-lg hover:shadow-xl hover:scale-105 transition-all text-sm font-semibold"
             onClick={() => setNewTaskModalOpen(true)}
          >
            <Plus size={16} />
            新建任务
          </button>
        </header>

        {/* Kanban Board */}
        <div className="flex-1 overflow-x-auto overflow-y-hidden px-8 pb-8">
          <div className="flex h-full gap-8 min-w-[800px]">
            {columns.map(status => (
              <div key={status} className="flex-1 flex flex-col min-w-[300px] h-full">
                {/* Column Header */}
                <div className="flex items-center justify-between mb-4 px-1">
                  <h3 className="text-sm font-bold text-gray-500 uppercase tracking-wider">{status}</h3>
                  <span className="text-xs font-semibold bg-gray-200/50 text-gray-500 px-2 py-0.5 rounded-md">
                    {filteredTasks.filter(t => t.status === status).length}
                  </span>
                </div>

                {/* Column Content */}
                <div className="flex-1 overflow-y-auto pr-2 pb-4 space-y-3 scrollbar-hide">
                  {filteredTasks
                    .filter(t => t.status === status)
                    .map(task => (
                      <TaskCard 
                        key={task.id} 
                        task={task} 
                        onMove={handleMoveTask}
                        onDelete={handleDeleteTask}
                      />
                    ))
                  }

                  {/* Empty State for Column */}
                  {filteredTasks.filter(t => t.status === status).length === 0 && (
                    <div className="h-32 border-2 border-dashed border-gray-200 rounded-2xl flex items-center justify-center text-gray-300 text-sm font-medium">
                      暂无
                    </div>
                  )}
                </div>
              </div>
            ))}
          </div>
        </div>
      </main>

      {/* New Task Modal */}
      <NewTaskModal 
        isOpen={isNewTaskModalOpen}
        onClose={() => setNewTaskModalOpen(false)}
        onCreate={handleCreateTask}
      />
    </div>
  );
};

export default App;

