      ? tasks 
      : tasks.filter(t => t.category === activeCategory);
  }, [tasks, activeCategory]);

  const columns: Status[] = ['待办', '进行中', '已完成'];

  // --- Handlers ---
  const handleMoveTask = (id: string, newStatus: Status) => {
    setTasks(prev => prev.map(t => t.id === id ? { ...t, status: newStatus } : t));
  };

  const handleDeleteTask = (id: string) => {
     setTasks(prev => prev.filter(t => t.id !== id));
  };

  const handleCreateTask = (taskData: Omit<Task, 'id' | 'createdAt' | 'status' | 'subtasks'>) => {
    const newTask: Task = {
      id: `task-${Date.now()}`,
      createdAt: Date.now(),
      status: '待办',
      subtasks: [], // Default empty subtasks for manual creation
      ...taskData
    };
    setTasks(prev => [newTask, ...prev]);
  };

  const handleAIPlanCreated = (result: AIAnalysisResult) => {
    const newTasks: Task[] = result.tasks.map((t, index) => ({
      id: `ai-task-${Date.now()}-${index}`,
      title: t.title,
      description: index === 0 ? `[核心目标: ${result.coreGoal}]\n\n${result.strategicAdvice}` : undefined,
      category: '工作',
      priority: t.priority,
      status: '待办',
      duration: t.duration,
      subtasks: t.steps.map((step, si) => ({
        id: `ai-sub-${Date.now()}-${index}-${si}`,
        title: step,
        completed: false
      })),
      createdAt: Date.now()
    }));

    setTasks(prev => [...newTasks, ...prev]);
  };

  // --- Render Helpers ---
  const renderNavButton = (cat: Category | '全部', icon: React.ReactNode, label: string) => {
    const isActive = activeCategory === cat;
    return (
      <button 
        onClick={() => setActiveCategory(cat)}
        className={`
          flex items-center gap-3 w-full px-4 py-3 rounded-xl transition-all duration-200 group
          ${isActive 
            ? 'bg-white shadow-sm text-gray-900 font-semibold' 
            : 'text-gray-500 hover:bg-white/50 hover:text-gray-700'
          }
        `}
      >
        <span className={`${isActive ? 'text-indigo-600' : 'text-gray-400 group-hover:text-gray-500'}`}>
          {icon}
        </span>
        <span className="text-sm">{label}</span>
        {isActive && <div className="ml-auto w-1.5 h-1.5 rounded-full bg-indigo-600" />}
      </button>
    );
  };

  return (
    <div className="flex h-screen w-full bg-[#F5F5F7] text-gray-900 overflow-hidden font-sans">

      {/* Sidebar - Glassmorphism */}
      <aside className="w-64 h-full hidden md:flex flex-col p-6 border-r border-gray-200/60 bg-[#F5F5F7]/50 backdrop-blur-md">
        <div className="mb-10 pl-2">
          <h1 className="text-2xl font-bold tracking-tight text-gray-900 flex items-center gap-2">
             <div className="w-3 h-3 bg-black rounded-full" />
             Focus.
          </h1>
          <p className="text-xs text-gray-400 font-medium mt-1 ml-5 tracking-wide">二阶段：本地化</p>
        </div>

        <nav className="space-y-2 flex-1">
          {renderNavButton('全部', <LayoutGrid size={18} />, '总览')}
          <div className="pt-4 pb-2 pl-4 text-[10px] font-bold text-gray-400 uppercase tracking-widest">领域</div>
          {renderNavButton('工作', <Briefcase size={18} />, '工作')}
          {renderNavButton('生活', <Coffee size={18} />, '生活')}
          {renderNavButton('创意', <Palette size={18} />, '创意')}
        </nav>

        <div className="mt-auto">
          {/* Sidebar bottom area can be used for settings later */}
        </div>
      </aside>

      {/* Main Content */}
      <main className="flex-1 h-full overflow-hidden flex flex-col relative">
        {/* Mobile Header */}
        <div className="md:hidden flex items-center justify-between p-6 pb-2">
          <h1 className="text-2xl font-bold">Focus.</h1>
          <button onClick={() => setNewTaskModalOpen(true)} className="p-2 bg-black text-white rounded-full shadow-lg">
            <Plus size={20} />
          </button>
        </div>

        {/* Board Header */}
        <header className="px-8 py-6 flex items-end justify-between shrink-0">
          <div>
            <h2 className="text-3xl font-bold text-gray-900 tracking-tight mb-1">
              {activeCategory === '全部' ? '总览' : activeCategory}
            </h2>
            <p className="text-gray-500 font-medium text-sm">
              {filteredTasks.length} 个待办任务 • 保持专注 (Stay Focused)
            </p>
          </div>

          <div className="hidden md:flex items-center gap-3">
            <button 
              className="flex items-center gap-2 bg-indigo-600 text-white px-5 py-2.5 rounded-full shadow-lg hover:shadow-xl hover:scale-105 transition-all text-sm font-semibold"
              onClick={() => setChiefModalOpen(true)}
            >
              <BrainCircuit size={16} />
              AI 规划
            </button>
            <button 
               className="flex items-center gap-2 bg-black text-white px-5 py-2.5 rounded-full shadow-lg hover:shadow-xl hover:scale-105 transition-all text-sm font-semibold"
               onClick={() => setNewTaskModalOpen(true)}
            >
              <Plus size={16} />
              新建任务
            </button>
          </div>
        </header>

        {/* Kanban Board */}
        <div className="flex-1 overflow-x-auto overflow-y-hidden px-8 pb-8">
          <div className="flex h-full gap-8 min-w-[800px]">
            {columns.map(status => (
              <div key={status} className="flex-1 flex flex-col min-w-[300px] h-full">
                {/* Column Header */}
                <div className="flex items-center justify-between mb-4 px-1">
                  <h3 className="text-sm font-bold text-gray-500 uppercase tracking-wider">{status}</h3>
                  <span className="text-xs font-semibold bg-gray-200/50 text-gray-500 px-2 py-0.5 rounded-md">
                    {filteredTasks.filter(t => t.status === status).length}
                  </span>
                </div>

                {/* Column Content */}
                <div className="flex-1 overflow-y-auto pr-2 pb-4 space-y-3 scrollbar-hide">
                  {filteredTasks
                    .filter(t => t.status === status)
                    .map(task => (
                      <TaskCard 
                        key={task.id} 
                        task={task} 
                        onMove={handleMoveTask}
                        onDelete={handleDeleteTask}
                      />
                    ))
                  }

                  {/* Empty State for Column */}
                  {filteredTasks.filter(t => t.status === status).length === 0 && (
                    <div className="h-32 border-2 border-dashed border-gray-200 rounded-2xl flex items-center justify-center text-gray-300 text-sm font-medium">
                      暂无
                    </div>
                  )}
                </div>
              </div>
            ))}
          </div>
        </div>
      </main>

      {/* New Task Modal */}
      <NewTaskModal 
        isOpen={isNewTaskModalOpen}
        onClose={() => setNewTaskModalOpen(false)}
        onCreate={handleCreateTask}
      />

      {/* Chief of Staff Modal */}
      <ChiefOfStaffModal
        isOpen={isChiefModalOpen}
        onClose={() => setChiefModalOpen(false)}
        onPlanCreated={handleAIPlanCreated}
      />
    </div>
  );
};

export default App;

import React from 'react';
import { 
  X, 
  Clock, 
  CheckCircle2, 
  Briefcase, 
  Coffee, 
  Palette, 
  AlertCircle, 
  Trash2,
  Calendar
} from './Icons';
import { Task, Priority, Category, Status } from '../types';

interface TaskDetailModalProps {
  task: Task | null;
  onClose: () => void;
  onUpdate: (updatedTask: Task) => void;
  onDelete: (id: string) => void;
}

const PriorityBadge: React.FC<{ priority: Priority }> = ({ priority }) => {
  const colors = {
    'P0': 'bg-red-50 text-red-600 border-red-100 ring-red-500/10',
    'P1': 'bg-orange-50 text-orange-600 border-orange-100 ring-orange-500/10',
    'P2': 'bg-blue-50 text-blue-600 border-blue-100 ring-blue-500/10',
    'P3': 'bg-gray-50 text-gray-500 border-gray-100 ring-gray-500/10',
  };

  return (
    <span className={`text-xs font-semibold px-2.5 py-1 rounded-md border ring-1 inset-0 ${colors[priority]}`}>
      {priority}
    </span>
  );
};

const CategoryIcon: React.FC<{ category: Category }> = ({ category }) => {
  switch (category) {
    case '工作': return <Briefcase size={16} className="text-gray-500" />;
    case '生活': return <Coffee size={16} className="text-gray-500" />;
    case '创意': return <Palette size={16} className="text-gray-500" />;
    default: return null;
  }
};

export const TaskDetailModal: React.FC<TaskDetailModalProps> = ({ task, onClose, onUpdate, onDelete }) => {
  if (!task) return null;

  const handleToggleSubtask = (subtaskId: string) => {
    const updatedSubtasks = task.subtasks.map(st => 
      st.id === subtaskId ? { ...st, completed: !st.completed } : st
    );
    onUpdate({ ...task, subtasks: updatedSubtasks });
  };

  const handleStatusChange = (newStatus: Status) => {
    onUpdate({ ...task, status: newStatus });
  };

  const handleDelete = () => {
    if (window.confirm('确定要删除这个任务吗？')) {
      onDelete(task.id);
      onClose();
    }
  };

  return (
    <div className="fixed inset-0 z-50 flex items-center justify-center p-4">
      {/* Blur Backdrop */}
      <div 
        className="absolute inset-0 bg-gray-900/30 backdrop-blur-md transition-opacity duration-300" 
        onClick={onClose} 
      />

      {/* Modal Card - Designed for Immersion */}
      <div className="relative w-full max-w-2xl bg-white rounded-[32px] shadow-2xl overflow-hidden animate-in fade-in zoom-in-95 duration-200 border border-white/40 flex flex-col max-h-[85vh]">

        {/* Header Area */}
        <div className="px-8 pt-8 pb-4 flex justify-between items-start">
          <div className="flex flex-col gap-3">
            <div className="flex items-center gap-3">
              <span className="inline-flex items-center gap-1.5 px-3 py-1 bg-gray-100 rounded-full text-xs font-medium text-gray-600">
                <CategoryIcon category={task.category} />
                {task.category}
              </span>
              <PriorityBadge priority={task.priority} />
            </div>
            <h2 className="text-2xl font-bold text-gray-900 leading-tight">{task.title}</h2>
          </div>

          <button 
            onClick={onClose} 
            className="w-8 h-8 flex items-center justify-center rounded-full bg-gray-100 hover:bg-gray-200 text-gray-500 transition-colors"
          >
            <X size={18} />
          </button>
        </div>

        {/* Scrollable Content */}
        <div className="flex-1 overflow-y-auto px-8 py-2 space-y-8 scrollbar-hide">

          {/* Metadata Row */}
          <div className="flex items-center gap-6 text-sm text-gray-500 border-b border-gray-100 pb-6">
            <div className="flex items-center gap-2">
              <Clock size={16} />
              <span>{task.duration || '无时长限制'}</span>
            </div>
            <div className="flex items-center gap-2">
              <Calendar size={16} />
              <span>创建于 {new Date(task.createdAt).toLocaleDateString()}</span>
            </div>
          </div>

          {/* Description */}
          {task.description && (
             <div className="space-y-2">
               <h3 className="text-xs font-bold text-gray-400 uppercase tracking-widest">任务详情</h3>
               <p className="text-gray-700 text-base leading-relaxed whitespace-pre-line">
                 {task.description}
               </p>
             </div>
          )}

          {/* Subtasks Section */}
          <div className="space-y-3">
             <h3 className="text-xs font-bold text-gray-400 uppercase tracking-widest flex items-center justify-between">
                <span>检查清单</span>
                <span className="text-gray-300">{task.subtasks.filter(t => t.completed).length}/{task.subtasks.length}</span>
             </h3>

             {task.subtasks.length > 0 ? (
               <div className="bg-gray-50 rounded-2xl p-2 space-y-1">
                 {task.subtasks.map(st => (
                   <div 
                      key={st.id} 
                      onClick={() => handleToggleSubtask(st.id)}
                      className="group flex items-center gap-3 p-3 rounded-xl hover:bg-white hover:shadow-sm transition-all cursor-pointer"
                   >
                      <div className={`
                        w-5 h-5 rounded-full border-2 flex items-center justify-center transition-colors
                        ${st.completed ? 'bg-indigo-500 border-indigo-500' : 'border-gray-300 group-hover:border-indigo-400'}
                      `}>
                        {st.completed && <CheckCircle2 size={12} className="text-white" />}
                      </div>
                      <span className={`text-sm font-medium transition-all ${st.completed ? 'text-gray-400 line-through' : 'text-gray-700'}`}>
                        {st.title}
                      </span>
                   </div>
                 ))}
               </div>
             ) : (
                <div className="text-gray-400 text-sm italic py-2">无子任务</div>
             )}
          </div>
        </div>

        {/* Footer Actions */}
        <div className="p-6 bg-gray-50/80 backdrop-blur-xl border-t border-gray-100 flex justify-between items-center mt-auto">
           <button 
             onClick={handleDelete}
             className="flex items-center gap-2 text-red-500 hover:text-red-600 hover:bg-red-50 px-4 py-2 rounded-lg transition-colors text-sm font-medium"
           >
             <Trash2 size={16} />
             删除
           </button>

           <div className="flex bg-gray-200/50 p-1 rounded-full">
              {(['待办', '进行中', '已完成'] as Status[]).map(s => (
                <button
                  key={s}
                  onClick={() => handleStatusChange(s)}
                  className={`px-4 py-1.5 rounded-full text-xs font-semibold transition-all shadow-sm ${
                    task.status === s 
                      ? 'bg-white text-gray-900 shadow-sm' 
                      : 'text-gray-500 hover:text-gray-700 shadow-none'
                  }`}
                >
                  {s}
                </button>
              ))}
           </div>
        </div>
      </div>
    </div>
  );
};

import React from 'react';
import { Task, Priority } from '../types';
import { Clock, CheckCircle2, MoreHorizontal } from './Icons';

interface TaskCardProps {
  task: Task;
  onMove: (id: string, newStatus: Task['status']) => void;
  onDelete: (id: string) => void;
  onClick: (task: Task) => void;
}

const PriorityBadge: React.FC<{ priority: Priority }> = ({ priority }) => {
  const colors = {
    'P0': 'bg-red-50 text-red-600 border-red-100',
    'P1': 'bg-orange-50 text-orange-600 border-orange-100',
    'P2': 'bg-blue-50 text-blue-600 border-blue-100',
    'P3': 'bg-gray-50 text-gray-500 border-gray-100',
  };

  return (
    <span className={`text-[10px] font-semibold px-2 py-0.5 rounded-full border ${colors[priority]}`}>
      {priority}
    </span>
  );
};

export const TaskCard: React.FC<TaskCardProps> = ({ task, onMove, onDelete, onClick }) => {
  const handleAction = (e: React.MouseEvent, action: () => void) => {
    e.stopPropagation(); // Prevent opening the details modal
    action();
  };

  return (
    <div 
      onClick={() => onClick(task)}
      className="group bg-white p-4 rounded-2xl shadow-[0_2px_8px_rgba(0,0,0,0.04)] hover:shadow-[0_8px_24px_rgba(0,0,0,0.12)] hover:-translate-y-1 transition-all duration-300 border border-gray-100/50 flex flex-col gap-3 relative animate-in fade-in slide-in-from-bottom-2 cursor-pointer active:scale-[0.98]"
    >

      {/* Header */}
      <div className="flex justify-between items-start">
        <div className="flex gap-2 items-center">
          <PriorityBadge priority={task.priority} />
          {task.duration && (
            <span className="flex items-center gap-1 text-[10px] text-gray-400 font-medium">
              <Clock size={10} />
              {task.duration}
            </span>
          )}
        </div>
        <button 
          onClick={(e) => handleAction(e, () => onDelete(task.id))}
          className="text-gray-300 hover:text-red-500 transition-colors opacity-0 group-hover:opacity-100 p-1 -mr-1"
          title="删除任务"
        >
          <MoreHorizontal size={16} />
        </button>
      </div>

      {/* Content */}
      <div>
        <h3 className="text-gray-900 font-semibold text-sm leading-snug mb-1 group-hover:text-indigo-600 transition-colors">{task.title}</h3>
        {task.description && (
          <p className="text-gray-500 text-xs line-clamp-2">{task.description}</p>
        )}
      </div>

      {/* Subtasks Preview (if any) */}
      {task.subtasks.length > 0 && (
        <div className="bg-gray-50/50 rounded-lg p-2 space-y-1 group-hover:bg-indigo-50/30 transition-colors">
          {task.subtasks.slice(0, 2).map(st => (
            <div key={st.id} className="flex items-center gap-2 text-xs text-gray-500">
               <div className={`w-1.5 h-1.5 rounded-full ${st.completed ? 'bg-green-400' : 'bg-gray-300'}`} />
               <span className={st.completed ? 'line-through opacity-60' : ''}>{st.title}</span>
            </div>
          ))}
          {task.subtasks.length > 2 && (
            <div className="text-[10px] text-gray-400 pl-3.5">还有 {task.subtasks.length - 2} 个步骤</div>
          )}
        </div>
      )}

      {/* Footer Actions */}
      <div className="flex items-center justify-between pt-1 border-t border-gray-50 mt-1 opacity-60 group-hover:opacity-100 transition-opacity">
        <span className="text-[10px] font-semibold text-gray-400 uppercase tracking-wider">{task.category}</span>

        {/* Simple State Mover */}
        <div className="flex gap-1">
           {task.status !== '待办' && (
             <button onClick={(e) => handleAction(e, () => onMove(task.id, '待办'))} className="w-6 h-6 rounded-full hover:bg-gray-100 flex items-center justify-center text-gray-400" title="移至待办">
               <div className="w-2 h-2 rounded-full border border-gray-400" />
             </button>
           )}
           {task.status !== '进行中' && (
             <button onClick={(e) => handleAction(e, () => onMove(task.id, '进行中'))} className="w-6 h-6 rounded-full hover:bg-blue-50 flex items-center justify-center text-blue-500" title="开始进行">
               <div className="w-2 h-2 rounded-full bg-blue-400" />
             </button>
           )}
           {task.status !== '已完成' && (
             <button onClick={(e) => handleAction(e, () => onMove(task.id, '已完成'))} className="w-6 h-6 rounded-full hover:bg-green-50 flex items-center justify-center text-green-500" title="标记完成">
               <CheckCircle2 size={14} />
             </button>
           )}
        </div>
      </div>
    </div>
  );
};

import React, { useState, useMemo } from 'react';
import { 
  LayoutGrid, 
  Briefcase, 
  Coffee, 
  Palette, 
  Plus,
  BrainCircuit
} from './components/Icons';
import { Task, Category, Status, AIAnalysisResult } from './types';
import { TaskCard } from './components/TaskCard';
import { NewTaskModal } from './components/NewTaskModal';
import { ChiefOfStaffModal } from './components/ChiefOfStaffModal';
import { TaskDetailModal } from './components/TaskDetailModal';

const App: React.FC = () => {
  // --- State ---
  const [tasks, setTasks] = useState<Task[]>([
    {
      id: '1',
      title: '回顾季度目标',
      category: '工作',
      priority: 'P1',
      status: '待办',
      duration: '45分钟',
      subtasks: [],
      createdAt: Date.now()
    },
    {
      id: '2',
      title: '绘制新 Logo 概念草图',
      category: '创意',
      priority: 'P2',
      status: '进行中',
      duration: '2小时',
      subtasks: [{ id: 's1', title: '情绪板收集', completed: true }, { id: 's2', title: '草图绘制', completed: false }],
      createdAt: Date.now()
    }
  ]);

  const [activeCategory, setActiveCategory] = useState<Category | '全部'>('全部');
  const [isNewTaskModalOpen, setNewTaskModalOpen] = useState(false);
  const [isChiefModalOpen, setChiefModalOpen] = useState(false);
  const [selectedTask, setSelectedTask] = useState<Task | null>(null);

  // --- Computed ---
  const filteredTasks = useMemo(() => {
    return activeCategory === '全部' 
      ? tasks 
      : tasks.filter(t => t.category === activeCategory);
  }, [tasks, activeCategory]);

  const columns: Status[] = ['待办', '进行中', '已完成'];

  // --- Handlers ---
  const handleMoveTask = (id: string, newStatus: Status) => {
    setTasks(prev => prev.map(t => t.id === id ? { ...t, status: newStatus } : t));
    // If the moved task is currently open in modal, update it there too
    if (selectedTask && selectedTask.id === id) {
       setSelectedTask(prev => prev ? { ...prev, status: newStatus } : null);
    }
  };

  const handleDeleteTask = (id: string) => {
     setTasks(prev => prev.filter(t => t.id !== id));
     if (selectedTask?.id === id) setSelectedTask(null);
  };

  const handleCreateTask = (taskData: Omit<Task, 'id' | 'createdAt' | 'status' | 'subtasks'>) => {
    const newTask: Task = {
      id: `task-${Date.now()}`,
      createdAt: Date.now(),
      status: '待办',
      subtasks: [], // Default empty subtasks for manual creation
      ...taskData
    };
    setTasks(prev => [newTask, ...prev]);
  };

  const handleUpdateTask = (updatedTask: Task) => {
    setTasks(prev => prev.map(t => t.id === updatedTask.id ? updatedTask : t));
    setSelectedTask(updatedTask);
  };

  const handleAIPlanCreated = (result: AIAnalysisResult) => {
    const newTasks: Task[] = result.tasks.map((t, index) => ({
      id: `ai-task-${Date.now()}-${index}`,
      title: t.title,
      description: index === 0 ? `[核心目标: ${result.coreGoal}]\n\n${result.strategicAdvice}` : undefined,
      category: '工作',
      priority: t.priority,
      status: '待办',
      duration: t.duration,
      subtasks: t.steps.map((step, si) => ({
        id: `ai-sub-${Date.now()}-${index}-${si}`,
        title: step,
        completed: false
      })),
      createdAt: Date.now()
    }));

    setTasks(prev => [...newTasks, ...prev]);
  };

  // --- Render Helpers ---
  const renderNavButton = (cat: Category | '全部', icon: React.ReactNode, label: string) => {
    const isActive = activeCategory === cat;
    return (
      <button 
        onClick={() => setActiveCategory(cat)}
        className={`
          flex items-center gap-3 w-full px-4 py-3 rounded-xl transition-all duration-200 group
          ${isActive 
            ? 'bg-white shadow-sm text-gray-900 font-semibold' 
            : 'text-gray-500 hover:bg-white/50 hover:text-gray-700'
          }
        `}
      >
        <span className={`${isActive ? 'text-indigo-600' : 'text-gray-400 group-hover:text-gray-500'}`}>
          {icon}
        </span>
        <span className="text-sm">{label}</span>
        {isActive && <div className="ml-auto w-1.5 h-1.5 rounded-full bg-indigo-600" />}
      </button>
    );
  };

  return (
    <div className="flex h-screen w-full bg-[#F5F5F7] text-gray-900 overflow-hidden font-sans">

      {/* Sidebar - Glassmorphism */}
      <aside className="w-64 h-full hidden md:flex flex-col p-6 border-r border-gray-200/60 bg-[#F5F5F7]/50 backdrop-blur-md">
        <div className="mb-10 pl-2">
          <h1 className="text-2xl font-bold tracking-tight text-gray-900 flex items-center gap-2">
             <div className="w-3 h-3 bg-black rounded-full" />
             Focus.
          </h1>
          <p className="text-xs text-gray-400 font-medium mt-1 ml-5 tracking-wide">二阶段：本地化</p>
        </div>

