const CACHE_NAME = 'focus-app-v1';
const urlsToCache = [
  '/',
  '/index.html',
  '/index.tsx',
  '/types.ts',
  '/manifest.json'
];

self.addEventListener('install', event => {
  event.waitUntil(
    caches.open(CACHE_NAME)
      .then(cache => {
        return cache.addAll(urlsToCache);
      })
  );
});

self.addEventListener('fetch', event => {
  event.respondWith(
