请帮我再次升级 ZiweiModal 的核心计算逻辑。用户反馈上一版的数据详细度不够。
请用下面这段代码**完全替换**之前的 `generateReport` 及相关辅助函数。

这次升级的目标是：在保留八字的基础上，将紫微斗数的详细度恢复到“文墨天机”的水平（增加神煞、纳音、详细四化箭头）。

// ==========================================
// iZiwei Fusion X (Max Detail Logic)
// ==========================================
import { Solar } from 'lunar-javascript';
import { astro } from 'iztro';

// 1. 静态真理表 (保持不变)
const ZHI_HIDDEN = {
    '子':['癸'], '丑':['己','癸','辛'], '寅':['甲','丙','戊'], '卯':['乙'],
    '辰':['戊','乙','癸'], '巳':['丙','戊','庚'], '午':['丁','己'], '未':['己','丁','乙'],
    '申':['庚','壬','戊'], '酉':['辛'], '戌':['戊','辛','丁'], '亥':['壬','甲']
};
const SHI_SHEN_TABLE = {
    '甲':{'甲':'比肩','乙':'劫财','丙':'食神','丁':'伤官','戊':'偏财','己':'正财','庚':'七杀','辛':'正官','壬':'偏印','癸':'正印'},
    '乙':{'甲':'劫财','乙':'比肩','丙':'伤官','丁':'食神','戊':'正财','己':'偏财','庚':'正官','辛':'七杀','壬':'正印','癸':'偏印'},
    '丙':{'甲':'偏印','乙':'正印','丙':'比肩','丁':'劫财','戊':'食神','己':'伤官','庚':'偏财','辛':'正财','壬':'七杀','癸':'正官'},
    '丁':{'甲':'正印','乙':'偏印','丙':'劫财','丁':'比肩','戊':'伤官','己':'食神','庚':'正财','辛':'偏财','壬':'正官','癸':'七杀'},
    '戊':{'甲':'七杀','乙':'正官','丙':'偏印','丁':'正印','戊':'比肩','己':'劫财','庚':'食神','辛':'伤官','壬':'偏财','癸':'正财'},
    '己':{'甲':'正官','乙':'七杀','丙':'正印','丁':'偏印','戊':'劫财','己':'比肩','庚':'伤官','辛':'食神','壬':'正财','癸':'偏财'},
    '庚':{'甲':'偏财','乙':'正财','丙':'七杀','丁':'正官','戊':'偏印','己':'正印','庚':'比肩','辛':'劫财','壬':'食神','癸':'伤官'},
    '辛':{'甲':'正财','乙':'偏财','丙':'正官','丁':'七杀','戊':'正印','己':'偏印','庚':'劫财','辛':'比肩','壬':'伤官','癸':'食神'},
    '壬':{'甲':'食神','乙':'伤官','丙':'偏财','丁':'正财','戊':'七杀','己':'正官','庚':'偏印','辛':'正印','壬':'比肩','癸':'劫财'},
    '癸':{'甲':'伤官','乙':'食神','丙':'正财','丁':'偏财','戊':'正官','己':'七杀','庚':'正印','辛':'偏印','壬':'劫财','癸':'比肩'}
};
const ZW_SIHUA = {
    '甲': {禄:'廉贞',权:'破军',科:'武曲',忌:'太阳'},
    '乙': {禄:'天机',权:'天梁',科:'紫微',忌:'太阴'},
    '丙': {禄:'天同',权:'天机',科:'文昌',忌:'廉贞'},
    '丁': {禄:'太阴',权:'天同',科:'天机',忌:'巨门'},
    '戊': {禄:'贪狼',权:'太阴',科:'右弼',忌:'天机'},
    '己': {禄:'武曲',权:'贪狼',科:'天梁',忌:'文曲'},
    '庚': {禄:'太阳',权:'武曲',科:'太阴',忌:'天同'},
    '辛': {禄:'巨门',权:'太阳',科:'文曲',忌:'文昌'},
    '壬': {禄:'天梁',权:'紫微',科:'左辅',忌:'武曲'},
    '癸': {禄:'破军',权:'巨门',科:'太阴',忌:'贪狼'}
};

// 2. 辅助工具
function safeStr(input: any): string {
    if (input === null || input === undefined) return "";
    if (typeof input === 'string') return input;
    if (typeof input.getName === 'function') return input.getName();
    if (typeof input.toString === 'function') return input.toString();
    return String(input);
}
function getTenGods(dayGan: string, targetGan: string) {
    // @ts-ignore
    return (SHI_SHEN_TABLE[dayGan] && SHI_SHEN_TABLE[dayGan][targetGan]) || "?";
}
const BRANCHES = ['子','丑','寅','卯','辰','巳','午','未','申','酉','戌','亥'];
function getOppositeBranch(branch: string) {
    const idx = BRANCHES.indexOf(branch);
    return BRANCHES[(idx + 6) % 12];
}

// 3. 核心生成函数 (Max Detail Edition)
const generateReport = (dateInput: string, gender: '男'|'女') => {
    try {
        const d = new Date(dateInput);

        // --- A. 八字计算 (增加纳音) ---
        const solar = Solar.fromYmdHms(d.getFullYear(), d.getMonth() + 1, d.getDate(), d.getHours(), d.getMinutes(), 0);
        const lunar = solar.getLunar();
        const bazi = lunar.getEightChar();
        bazi.setSect(2); 

        const dayMaster = safeStr(bazi.getDayGan()); 

        let r = `【命理深度分析报告 (Fusion X版)】\n`;
        r += `公历: ${d.toLocaleString('zh-CN')} (${gender})\n`;
        r += `农历: ${lunar.toString()}\n`;
        r += `------------------------------------------------\n\n`;

        // 八字渲染
        r += `## 第一部分：八字命盘 (BaZi)\n`;
        const renderPillar = (ganObj: any, zhiObj: any, name: string, nayin: string) => {
            const g = safeStr(ganObj);
            const z = safeStr(zhiObj);
            const tenGod = getTenGods(dayMaster, g);
            // @ts-ignore
            const hiddens = (ZHI_HIDDEN[z] || []).map(h => `${h}<${getTenGods(dayMaster, h)}>`).join(',');
            return `- ${name}柱: [${g}${z}] (${nayin})\n  * 十神: ${tenGod}\n  * 藏干: ${hiddens}`;
        };

        r += renderPillar(bazi.getYearGan(), bazi.getYearZhi(), '年', bazi.getYearNaYin());
        r += renderPillar(bazi.getMonthGan(), bazi.getMonthZhi(), '月', bazi.getMonthNaYin());

        const dayZ = safeStr(bazi.getDayZhi());
        // @ts-ignore
        const dayHiddens = (ZHI_HIDDEN[dayZ] || []).map(h => `${h}<${getTenGods(dayMaster, h)}>`).join(',');
        r += `- 日柱: [${dayMaster}${dayZ}] (${bazi.getDayNaYin()}) ★日元\n  * 藏干: ${dayHiddens}\n`;

        r += renderPillar(bazi.getTimeGan(), bazi.getTimeZhi(), '时', bazi.getTimeNaYin());
        r += '\n';

        // 大运
        r += `### 大运 (起运: ${bazi.getYun(gender==='男'?1:0).getStartYear()}年)\n`;
        const yun = bazi.getYun(gender === '男' ? 1 : 0);
        const daYuns = yun.getDaYun();
        for (let i = 0; i < 8; i++) {
            const dy = daYuns[i];
            let gz = "";
            try { gz = dy.getGanZhi(); } catch(e) { gz = "??"; }
            if (gz && gz.length >= 1) {
                r += `[${dy.getStartAge()}-${dy.getEndAge()}岁] ${gz}运 <${getTenGods(dayMaster, gz.charAt(0))}>\n`;
            }
        }

        // --- B. 紫微计算 (增加神煞、杂曜分类) ---
        r += `\n------------------------------------------------\n`;
        r += `## 第二部分：紫微斗数 (Ziwei Pro)\n\n`;
        const hourIdx = Math.floor((d.getHours() + 1) / 2) % 12;
        const iztroDate = `${d.getFullYear()}-${d.getMonth() + 1}-${d.getDate()}`;
        const astrolabe = astro.bySolar(iztroDate, hourIdx, gender, true, 'zh-CN');
        r += `五行局: ${astrolabe.fiveElementsClass} | 命主: ${astrolabe.soul} | 身主: ${astrolabe.body}\n\n`;

        astrolabe.palaces.forEach(p => {
            let flags = [];
            if (p.isBodyPalace) flags.push("★身宫");
            if (p.isOriginalPalace) flags.push("★来因宫");
            r += `### ${p.name} [${p.heavenlyStem}${p.earthlyBranch}] ${flags.join(' ')}\n`;

            // 星曜处理函数
            const processStar = (s: any) => {
                let txt = s.name;
                if (s.brightness) txt += `[${s.brightness}]`;
                if (s.mutagen) txt += `[生年${s.mutagen}]`;

                // 离心自化 (本宫干 -> 星)
                // @ts-ignore
                const selfMap = ZW_SIHUA[p.heavenlyStem];
                if (selfMap) {
                     if(selfMap.禄===s.name) txt+='[↓禄]';
                     if(selfMap.权===s.name) txt+='[↓权]';
                     if(selfMap.科===s.name) txt+='[↓科]';
                     if(selfMap.忌===s.name) txt+='[↓忌]';
                }

                // 向心自化 (对宫干 -> 星)
                const oppBranch = getOppositeBranch(p.earthlyBranch);
                const oppPalace = astrolabe.palaces.find(tp => tp.earthlyBranch === oppBranch);
                if (oppPalace) {
                    // @ts-ignore
                    const inMap = ZW_SIHUA[oppPalace.heavenlyStem];
                    if (inMap) {
                        if(inMap.禄===s.name) txt+='[↑禄]';
                        if(inMap.权===s.name) txt+='[↑权]';
                        if(inMap.科===s.name) txt+='[↑科]';
                        if(inMap.忌===s.name) txt+='[↑忌]';
                    }
                }
                return txt;
            };

            const majors = (p.majorStars||[]).map(processStar).join(', ');
            const minors = (p.minorStars||[]).map(processStar).join(', ');
            const adhocs = (p.adhocStars||[]).map(s => s.name).join(', ');

            r += `  * 主星: ${majors || "无"}\n`;
            if(minors) r += `  * 辅星: ${minors}\n`;
            if(adhocs) r += `  * 杂曜: ${adhocs}\n`;

            // 神煞 (重要升级)
            let shenshas = [];
            if (p.changsheng) shenshas.push(`长生:${p.changsheng}`);
            if (p.doctor12) shenshas.push(`博士:${p.doctor12}`);
            if (p.suiqian12) shenshas.push(`岁前:${p.suiqian12}`);
            if (p.jiangqian12) shenshas.push(`将前:${p.jiangqian12}`);
            if (shenshas.length > 0) r += `  * 神煞: ${shenshas.join(' | ')}\n`;

            // 宫干四化
            // @ts-ignore
            const siHua = ZW_SIHUA[p.heavenlyStem];
            if (siHua) r += `  * 飞化: ${p.heavenlyStem}干 -> ${siHua.禄}禄、${siHua.权}权、${siHua.科}科、${siHua.忌}忌\n`;

            // 运限
            if (p.decadal) r += `  * 大限: ${p.decadal.range[0]} - ${p.decadal.range[1]} 岁\n`;
            r += `\n`;
        });

        return r;
    } catch (e: any) {
        return "排盘出错: " + e.message;
    }
};

