请帮我再次升级 ZiweiModal 的核心逻辑。
用户反馈：1. 之前的输出格式太占篇幅，需要压缩行数。2. 缺少“当前时间”的运势描述（大运/流年）。

请用下面这段**V10 紧凑动态版**代码完全替换原有的 `generateReport` 及相关函数。
这段代码会自动计算用户当前的虚岁，并找出对应的八字大运和紫微流年宫位，且输出格式非常紧凑。

// ==========================================
// iZiwei Fusion V10 (Compact & Dynamic)
// ==========================================
import { Solar, Lunar } from 'lunar-javascript';
import { astro } from 'iztro';

// 1. 静态真理表 (保持不变，确保无报错)
const ZHI_HIDDEN = {'子':['癸'],'丑':['己','癸','辛'],'寅':['甲','丙','戊'],'卯':['乙'],'辰':['戊','乙','癸'],'巳':['丙','戊','庚'],'午':['丁','己'],'未':['己','丁','乙'],'申':['庚','壬','戊'],'酉':['辛'],'戌':['戊','辛','丁'],'亥':['壬','甲']};
const SHI_SHEN_TABLE = {'甲':{'甲':'比肩','乙':'劫财','丙':'食神','丁':'伤官','戊':'偏财','己':'正财','庚':'七杀','辛':'正官','壬':'偏印','癸':'正印'},'乙':{'甲':'劫财','乙':'比肩','丙':'伤官','丁':'食神','戊':'正财','己':'偏财','庚':'正官','辛':'七杀','壬':'正印','癸':'偏印'},'丙':{'甲':'偏印','乙':'正印','丙':'比肩','丁':'劫财','戊':'食神','己':'伤官','庚':'偏财','辛':'正财','壬':'七杀','癸':'正官'},'丁':{'甲':'正印','乙':'偏印','丙':'劫财','丁':'比肩','戊':'伤官','己':'食神','庚':'正财','辛':'偏财','壬':'正官','癸':'七杀'},'戊':{'甲':'七杀','乙':'正官','丙':'偏印','丁':'正印','戊':'比肩','己':'劫财','庚':'食神','辛':'伤官','壬':'偏财','癸':'正财'},'己':{'甲':'正官','乙':'七杀','丙':'正印','丁':'偏印','戊':'劫财','己':'比肩','庚':'伤官','辛':'食神','壬':'正财','癸':'偏财'},'庚':{'甲':'偏财','乙':'正财','丙':'七杀','丁':'正官','戊':'偏印','己':'正印','庚':'比肩','辛':'劫财','壬':'食神','癸':'伤官'},'辛':{'甲':'正财','乙':'偏财','丙':'正官','丁':'七杀','戊':'正印','己':'偏印','庚':'劫财','辛':'比肩','壬':'伤官','癸':'食神'},'壬':{'甲':'食神','乙':'伤官','丙':'偏财','丁':'正财','戊':'七杀','己':'正官','庚':'偏印','辛':'正印','壬':'比肩','癸':'劫财'},'癸':{'甲':'伤官','乙':'食神','丙':'正财','丁':'偏财','戊':'正官','己':'七杀','庚':'正印','辛':'偏印','壬':'劫财','癸':'比肩'}};
const ZW_SIHUA = {'甲':{禄:'廉贞',权:'破军',科:'武曲',忌:'太阳'},'乙':{禄:'天机',权:'天梁',科:'紫微',忌:'太阴'},'丙':{禄:'天同',权:'天机',科:'文昌',忌:'廉贞'},'丁':{禄:'太阴',权:'天同',科:'天机',忌:'巨门'},'戊':{禄:'贪狼',权:'太阴',科:'右弼',忌:'天机'},'己':{禄:'武曲',权:'贪狼',科:'天梁',忌:'文曲'},'庚':{禄:'太阳',权:'武曲',科:'太阴',忌:'天同'},'辛':{禄:'巨门',权:'太阳',科:'文曲',忌:'文昌'},'壬':{禄:'天梁',权:'紫微',科:'左辅',忌:'武曲'},'癸':{禄:'破军',权:'巨门',科:'太阴',忌:'贪狼'}};

// 2. 辅助工具
function safeStr(input: any): string {
    if (input === null || input === undefined) return "";
    if (typeof input === 'string') return input;
    if (typeof input.getName === 'function') return input.getName();
    return String(input);
}
function getTenGods(dayGan: string, targetGan: string) {
    // @ts-ignore
    return (SHI_SHEN_TABLE[dayGan] && SHI_SHEN_TABLE[dayGan][targetGan]) || "?";
}
const BRANCHES = ['子','丑','寅','卯','辰','巳','午','未','申','酉','戌','亥'];
function getOppositeBranch(branch: string) { return BRANCHES[(BRANCHES.indexOf(branch) + 6) % 12]; }

// 3. 核心生成函数 (Compact & Dynamic)
const generateReport = (dateInput: string, gender: '男'|'女') => {
    try {
        const d = new Date(dateInput);
        const today = new Date();
        const currentYear = today.getFullYear();

        // --- A. 八字与基础信息 ---
        const solar = Solar.fromYmdHms(d.getFullYear(), d.getMonth() + 1, d.getDate(), d.getHours(), d.getMinutes(), 0);
        const lunar = solar.getLunar();
        const bazi = lunar.getEightChar();
        bazi.setSect(2); 

        const dayMaster = safeStr(bazi.getDayGan());

        // 计算虚岁 (简单算法：当前年 - 出生年 + 1)
        const age = currentYear - d.getFullYear() + 1;

        // 获取当前流年干支
        const todayLunar = Lunar.fromDate(today);
        const currentLiuNian = todayLunar.getYearInGanZhi(); // e.g., '乙巳'
        const currentLiuNianZhi = todayLunar.getYearZhi(); // e.g., '巳'

        let r = `【八字紫微精简盘】公历:${d.toLocaleDateString()} ${gender} | 农历:${lunar.toString()} | 虚岁:${age}\n`;
        r += `------------------------------------------------\n`;

        // 1. 八字结构 (单行表格化)
        r += `【八字】(日元:${dayMaster})\n`;
        const renderPillarShort = (g:any, z:any, nayin:string) => {
            const gs = safeStr(g); const zs = safeStr(z);
            const tg = getTenGods(dayMaster, gs);
            // @ts-ignore
            const h = (ZHI_HIDDEN[zs]||[]).join(''); // 藏干只列名字，不列十神以节省空间
            return `[${gs}${zs}/${tg}]`; 
        };
        r += `年:${renderPillarShort(bazi.getYearGan(), bazi.getYearZhi(), bazi.getYearNaYin())} `;
        r += `月:${renderPillarShort(bazi.getMonthGan(), bazi.getMonthZhi(), bazi.getMonthNaYin())} `;
        r += `日:[${dayMaster}${safeStr(bazi.getDayZhi())}] `;
        r += `时:${renderPillarShort(bazi.getTimeGan(), bazi.getTimeZhi(), bazi.getTimeNaYin())}\n`;

        // 2. 当前运势锚点 (Dynamic Context)
        r += `【当前运程 (${currentYear}年/${age}岁)】\n`;

        // 八字大运定位
        const yun = bazi.getYun(gender === '男' ? 1 : 0);
        const daYuns = yun.getDaYun();
        let currentDaYunStr = "未起运";
        for (let i = 0; i < daYuns.length; i++) {
            const dy = daYuns[i];
            if (age >= dy.getStartAge() && age <= dy.getEndAge()) {
                currentDaYunStr = `${dy.getGanZhi()}运 (${dy.getStartAge()}-${dy.getEndAge()}岁) <${getTenGods(dayMaster, dy.getGanZhi().charAt(0))}>`;
                break;
            }
        }
        r += `> 八字: 大运[${currentDaYunStr}] | 流年[${currentLiuNian}]\n`;

        // 3. 紫微斗数 (单行模式)
        const hourIdx = Math.floor((d.getHours() + 1) / 2) % 12;
        const iztroDate = `${d.getFullYear()}-${d.getMonth() + 1}-${d.getDate()}`;
        const astrolabe = astro.bySolar(iztroDate, hourIdx, gender, true, 'zh-CN');
        r += `【紫微】${astrolabe.fiveElementsClass} | 命主:${astrolabe.soul} | 身主:${astrolabe.body}\n`;

        // 查找当前大限和流年宫位
        let daXianPalaceName = "";
        let liuNianPalaceName = "";

        // 遍历宫位生成紧凑文本
        astrolabe.palaces.forEach(p => {
            // 判断是否是当前大限宫
            const isCurrentDaXian = p.decadal && (age >= p.decadal.range[0] && age <= p.decadal.range[1]);
            if (isCurrentDaXian) daXianPalaceName = p.name;

            // 判断是否是当前流年宫 (地支相同)
            const isCurrentLiuNian = p.earthlyBranch === currentLiuNianZhi;
            if (isCurrentLiuNian) liuNianPalaceName = p.name;

            // 标记
            let marks = "";
            if (p.isBodyPalace) marks += "★身";
            if (p.isOriginalPalace) marks += "★来";
            if (isCurrentDaXian) marks += "【大限】";
            if (isCurrentLiuNian) marks += "【流年】";

            // 星曜 (精简版: 主星+吉凶, 不列全部杂曜)
            const stars = (p.majorStars||[]).map(s => {
                let t = s.name + (s.brightness ? `(${s.brightness})` : '');
                if (s.mutagen) t += `[生${s.mutagen}]`;
                // @ts-ignore
                const selfMap = ZW_SIHUA[p.heavenlyStem];
                if (selfMap && Object.values(selfMap).includes(s.name)) t += `[↓]`; // 离心简化标记
                return t;
            }).join(',');

            const minorStars = (p.minorStars||[]).map(s => s.name + (s.mutagen?`[生${s.mutagen}]`:'')).join(',');

            // 宫干四化 (极简)
            // @ts-ignore
            const sh = ZW_SIHUA[p.heavenlyStem];
            const fly = sh ? `${sh.禄}/${sh.权}/${sh.科}/${sh.忌}` : "";

            // 输出一行: [宫名] ★ 星曜 | 飞化
            r += `[${p.name}]${marks} ${stars} ${minorStars ? '| '+minorStars : ''} -> ${p.heavenlyStem}干化:${fly}\n`;
        });

        r += `> 紫微: 本大限命宫在[${daXianPalaceName}] | 今年流年命宫在[${liuNianPalaceName}]\n`;

        return r;
    } catch (e: any) {
        return "排盘出错: " + e.message;
    }
};

