const BRANCHES = ['子','丑','寅','卯','辰','巳','午','未','申','酉','戌','亥'];
// function getOppositeBranch(branch: string) { return BRANCHES[(BRANCHES.indexOf(branch) + 6) % 12]; } // Keep specifically if needed, otherwise simplified logic below uses direct mapping

// 3. 核心生成函数 (Compact & Dynamic)
const generateReportLogic = (dateInput: string, gender: '男'|'女') => {
    try {
        const d = new Date(dateInput);
        const today = new Date();
        const currentYear = today.getFullYear();

        // --- A. 八字与基础信息 ---
        const solar = Solar.fromYmdHms(d.getFullYear(), d.getMonth() + 1, d.getDate(), d.getHours(), d.getMinutes(), 0);
