        const bazi = lunar.getEightChar();
        bazi.setSect(2); 

        const dayMaster = safeStr(bazi.getDayGan());

        // 计算虚岁 (简单算法：当前年 - 出生年 + 1)
        const age = currentYear - d.getFullYear() + 1;

