        let daXianPalaceName = "";
        let liuNianPalaceName = "";

        // 遍历宫位生成紧凑文本
        astrolabe.palaces.forEach(p => {
            // 判断是否是当前大限宫
            const isCurrentDaXian = p.decadal && (age >= p.decadal.range[0] && age <= p.decadal.range[1]);
