        const astrolabe = astro.bySolar(iztroDate, hourIdx, gender, true, 'zh-CN');
        r += `> 紫微: ${astrolabe.fiveElementsClass} | 命主:${astrolabe.soul} | 身主:${astrolabe.body}\n\n`;

        let daXianName = "", liuNianName = "";

        astrolabe.palaces.forEach(p => {
